// Package imports:
import 'package:firebase_core/firebase_core.dart';

// Project imports:
import 'package:boorusama/core/infra/vendors/firebase_analytics.dart';
import 'package:boorusama/core/infra/vendors/firebase_crashlytics.dart';
import 'package:boorusama/firebase_options.dart';

Future<void> ensureFirebaseInitialized() async {
  await Firebase.initializeApp(
    options: DefaultFirebaseOptions.currentPlatform,
  );

  await initializeFirebaseCrashlytics();
  await initializeFirebaseAnalytics();
}
